mapper.insert:
insert into asset(
asset_name, asset_description, asset_type, asset_category, script_format, script
) values (
#{model.assetName}, #{model.assetDescription}, #{model.assetType}, #{model.assetCategory}, #{model.scriptFormat}, #{model.script}
)

mapper.select:
select * from asset a left join project_asset t on a.asset_id = t.asset_id

    project_id = #{projectId}
    or project_id = 1

select * from asset a left join project_asset t on a.asset_id = t.asset_id

    (project_id = #{projectId} or project_id = 1)
    and a.asset_type = #{assetType}

select * from asset a left join project_asset t on a.asset_id = t.asset_id

    (t.project_id = #{projectId} or t.project_id = 1)
    and t.project_asset_id = #{projectAssetId}

select * from asset a left join project_asset t on a.asset_id = t.asset_id

    (t.project_id = #{projectId} or t.project_id = 1)
    and a.asset_type = #{assetType}

select * from asset a left join project_asset t on a.asset_id = t.asset_id

    (t.project_id = #{projectId} or t.project_id = 1)
    and a.asset_category = #{assetCategory}

mapper.update:
update asset

    asset_name = #{model.assetName},
    asset_description = #{model.assetDescription},
    script = #{model.script}


    exists (
    select * from project_asset t

        t.project_id = #{projectId}
        and t.asset_id = asset.asset_id
        and t.project_asset_id = #{model.projectAssetId}

    )
